"use strict";
var Data_Eq = require("../Data.Eq/index.js");
var Data_Monoid = require("../Data.Monoid/index.js");
var Data_Semigroup = require("../Data.Semigroup/index.js");
var Effect_Console = require("../Effect.Console/index.js");
var Test_QuickCheck = require("../Test.QuickCheck/index.js");

// | - Left identity: `mempty <> x = x`
// | - Right identity: `x <> mempty = x`
var checkMonoid = function (dictMonoid) {
    return function (dictArbitrary) {
        return function (dictEq) {
            return function (v) {
                var rightIdentity = function (x) {
                    return Data_Eq.eq(dictEq)(Data_Semigroup.append(dictMonoid.Semigroup0())(x)(Data_Monoid.mempty(dictMonoid)))(x);
                };
                var leftIdentity = function (x) {
                    return Data_Eq.eq(dictEq)(Data_Semigroup.append(dictMonoid.Semigroup0())(Data_Monoid.mempty(dictMonoid))(x))(x);
                };
                return function __do() {
                    Effect_Console.log("Checking 'Left identity' law for Monoid")();
                    Test_QuickCheck["quickCheck'"](Test_QuickCheck.testableFunction(dictArbitrary)(Test_QuickCheck.testableBoolean))(1000)(leftIdentity)();
                    Effect_Console.log("Checking 'Right identity' law for Monoid")();
                    return Test_QuickCheck["quickCheck'"](Test_QuickCheck.testableFunction(dictArbitrary)(Test_QuickCheck.testableBoolean))(1000)(rightIdentity)();
                };
            };
        };
    };
};
module.exports = {
    checkMonoid: checkMonoid
};
